import React, { useState, useEffect } from 'react';

const Checkbox = ({ 
    checked,
    onChangeHandler,
}) => {
    const [isChecked, setIsChecked] = useState(checked);

    useEffect(() => {
        setIsChecked(checked);
    }, [checked]);

    return (
        <div>
            <input 
                type="checkbox" 
                checked={isChecked}
                onChange={(event) => {
                    setIsChecked(event.target.checked);
                    onChangeHandler(event.target.checked);
                }}
                
            />
        </div>
    );
}

export default Checkbox;