import React, { useState, useEffect } from 'react';

import Checkbox from 'components/checkbox';

const TableHeader = ({
    isCheckedCheckbox,
    onChangeHandler,
}) => {
    const [isChecked, setIsChecked] = useState(isCheckedCheckbox);

    useEffect(() => {
        setIsChecked(isCheckedCheckbox);
    }, [isCheckedCheckbox]);

    return (
        <div>
            Check all <Checkbox
                checked={isChecked}
                onChangeHandler={onChangeHandler}
            />
        </div>
    );
}

export default TableHeader;