import React, { useState, useEffect } from 'react';

import Checkbox from 'components/checkbox';

const TableRows = ({ 
    items,
    onChangeHandler,
}) => {

    const [list, setList] = useState(items);

    useEffect(() => {
        setList(items);
    }, [items]);

    console.log(list);
    
    return (
        <div>
            TableRows
            {list.map(el => (
                <div key={el.id}>
                    {el.id}
                    <Checkbox
                        checked={el.checked}
                        onChangeHandler={onChangeHandler} 
                    />
                </div>
                
            ))}
        </div>
    );
}

export default TableRows;